package by.epam.javawebtraining.kukareko.horseracebet.controller.helper;import by.epam.javawebtraining.kukareko.horseracebet.controller.handler.*;import by.epam.javawebtraining.kukareko.horseracebet.controller.handler.HorseCommand;import by.epam.javawebtraining.kukareko.horseracebet.util.ConfigurationManager;import javax.servlet.http.HttpServletRequest;import java.util.HashMap;import java.util.Map;import java.util.concurrent.locks.ReentrantLock;import java.util.regex.Matcher;import java.util.regex.Pattern;/** * @author Yulya Kukareko * @version 1.0 13 Apr 2019 */public class RequestHelper {    private static final ReentrantLock LOCK = new ReentrantLock();    private static RequestHelper instance;    private static ConfigurationManager configurationManager;    private Map<String, Command> commands = new HashMap<>();    private RequestHelper() {        commands.put("/auth/", new SignInCommand());        commands.put("/signup/", new SignUpCommand());        commands.put("/horses/", new HorseCommand());        commands.put("/races/", new RaceCommand());        commands.put("/sphorses/", new HorseStartingPriceCommand());        commands.put("/bets/", new BetCommand());        commands.put("/results/", new ResultCommand());        commands.put("/userbet/", new UserBetCommand());        commands.put("/user/", new UserCommand());        commands.put("/country/", new CountryCommand());        configurationManager = ConfigurationManager.getInstance();    }    public static RequestHelper getInstance() {        if (instance == null) {            LOCK.lock();            if (instance == null) {                instance = new RequestHelper();            }            LOCK.unlock();        }        return instance;    }    public Command getCommand(HttpServletRequest request) {        String defineControllerRegExp = configurationManager.getProperty("defineControllerRegExp");        Pattern pattern = Pattern.compile(defineControllerRegExp);        Matcher matcher = pattern.matcher(request.getRequestURL());        Command command = null;        if (matcher.find()) {            command = commands.get(matcher.group(0));        }        return command;    }}